// Componente feito para listar os post's da api
import { Link } from "react-router-dom";
import React, { useEffect, useState } from "react/cjs/react.production.min";
import { busca } from "../../../api/api";

function ListaPost({ url }) {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    busca(url, setPosts);
  }, [url]);
  return(
    <section className="posts container">
      { 
       posts.map((post)=> (
         <Link className={`cartao-post cartao-post--${post.categoria}`} to={`/posts/${post.id}`}>
           <article key={post.id}>
              <h3 className="cartao-post__titulo">
                {post.title}
              </h3>
              <p className="cartao-post__meta">{post.metadescription}</p>
           </article> 
         </Link>
       ))
      }
    </section>
  )
}

export default ListaPost;
