import React from "react";
import "./assets/css/base/base.css";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom/cjs/react-router-dom.min"; // Chama BrowserRouter de Router
import Home from "./paginas/Home";
import Sobre from "./paginas/Sobre";

// Route - Envelopa o componente

function App() {
  return (
    <>
      <Switch> {/* Faz com que, quando encontrar a path, pare de procurar outros iguais */}
        <Router>
          <Route exact path='/'> {/* Precisa ser exatamente /*/}
            <Home />
          </Route>
          <Route path='/sobre'>
            <Sobre />
          </Route>
        </Router>
      </Switch>
    </>
  );
}

export default App;
