import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { busca } from '../../../api/api';
import '../blog.css';

function ListaCategorias(){
    const [categorias, setCategorias] = useState([]);

    useEffect(()=>{ // Como vai fazer uma busca na api, temos que usar o useEffect
        busca('/categorias', setCategorias); // setCategorias atualiza o estado
    }, [])

    return(
        <ul className='lista-categorias container flex'>
            {categorias.map((categoria)=>(
                <Link to={`categoria/${categoria.id}`}>
                    <li className={`lista-categorias__categoria lista-categorias__categoria--${categoria.id}`}>
                        {categoria.nome}
                    </li>
                </Link>
            ))}
        </ul>
    );
}

export default ListaCategorias;
